pragma solidity ^0.5.11;
import "@openzeppelin/contracts/crowdsale/Crowdsale.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract DefiOptionsSeed is Crowdsale {
    using SafeMath for uint256;
    mapping(address => uint256) private _contributions;
    enum Tiers { Tier1, Tier2, Tier3, Tier4 }

    constructor(
        uint256 rate,
        address payable wallet,
        IERC20 token,
        uint256 relativeCap
    ) public Crowdsale(rate, wallet, token) {}

    function getShineTokenBalance(address beneficiary) internal view returns (uint256) {
        IERC20 shineToken = IERC20(0x1C7ede23b1361acC098A1e357C9085D131b34a01);
        return shineToken.balanceOf(beneficiary);
    }

    function getTier(uint256 shineBalance) internal view returns (Tiers){
        if(shineBalance >= 15000000000000000000000 && shineBalance < 50000000000000000000000){
            return Tiers.Tier1;
        }else if(shineBalance >= 50000000000000000000000 && shineBalance < 200000000000000000000000){
            return Tiers.Tier2;
        }else if(shineBalance >= 200000000000000000000000 && shineBalance < 400000000000000000000000){
            return Tiers.Tier3;
        }else if (shineBalance >= 400000000000000000000000){
            return Tiers.Tier4;
        }
    }

    function getContribution(address beneficiary) public view returns (uint256) {
        return _contributions[beneficiary];
    }

    function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
        super._preValidatePurchase(beneficiary, weiAmount);
        uint256 shineBalance = getShineTokenBalance(beneficiary);
        Tiers tier = getTier(shineBalance);
        require(tier == Tiers.Tier3, "You need to be Tier3 in order to participate");
        require(shineBalance >= 200000000000000000000000, "Minimum amount needed for seed sale is 200k SHN");
        //require(shineBalance);
        require(_contributions[beneficiary].add(weiAmount) <= 1000000000000000000, "IndividuallyCappedCrowdsale: beneficiary's cap exceeded");
        //require(weiAmount <= 1000000000000000000);
        // solhint-disable-next-line max-line-length
        //require(_contributions[beneficiary].add(weiAmount) <= _caps[beneficiary], "IndividuallyCappedCrowdsale: beneficiary's cap exceeded");
    }

     function _updatePurchasingState(address beneficiary, uint256 weiAmount) internal {
        super._updatePurchasingState(beneficiary, weiAmount);
        _contributions[beneficiary] = _contributions[beneficiary].add(weiAmount);
    }

}

